import "./ex2.css";
import TodoForm from "./Components/TodoForm/TodoForm";
import TodoListFilter from "./Components/TodoListFilter/TodoListFilter";
import TodoList from "./Components/TodoList/TodoList";
import { useEffect, useState } from "react";
import styled from "styled-components";

const MainDiv = styled.div`
  background: #fff;
  margin: 2rem 0 4rem 0;
  padding: 1rem;
  position: relative;
  box-shadow: 0 2px 4px 0 rgba(0, 0, 0, 0.2), 0 2.5rem 5rem 0 rgba(0, 0, 0, 0.1);

  > * {
    max-width: 50rem;
    margin-left: auto;
    margin-right: auto;
  }

  > form {
    max-width: 100%;
  }

  > h1 {
    display: block;
    max-width: 100%;
    text-align: center;
    margin: 0;
    margin-bottom: 1rem;
  }

  > * + * {
    margin-top: 2.5rem;
  }

  @media screen and (min-width: 550px) {
    padding: 4rem;
    > * + * {
      margin-top: 2.8rem;
    }
  }
`;

function App() {
  const [list, setList] = useState(["Eat", "Sleep", "Repeat"]);
  const [filter, setFilter] = useState("All");

  function add(element) {
    setList([...list, element]);
  }

  function edit(index, newelement) {
    const newlist = list.map((item, itemIndex) => {
      if (index === itemIndex) {
        return newelement;
      } else {
        return item;
      }
    });
    setList(newlist);
  }

  function remove(index) {
    var newlist = list.filter((item, itemIndex) => itemIndex !== index);
    setList(newlist);
    console.log(newlist);
  }

  useEffect(() => {
    // Update the document title using the browser API
    document.title = `To Do - ${list.length} tasks`;
  }, [list]);

  return (
    <MainDiv className="todoapp stack-large">
      <TodoForm add={add}></TodoForm>
      <TodoListFilter filter={filter} setFilter={setFilter}></TodoListFilter>
      <TodoList
        list={list}
        filter={filter}
        edit={edit}
        remove={remove}
      ></TodoList>
    </MainDiv>
  );
}

export default App;
