import React, { useState } from "react";

const Todo = (props) => {
  const [editable, setEditable] = useState(false);

  return (
    <li className="todo">
      {editable ? (
        <form class="stack-small">
          <div class="form-group">
            <label class="todo-label" htmlFor="todo-0">
              New name for {props.element}
            </label>
            <input id="todo-0" class="todo-text" type="text" value="" />
          </div>
          <div class="btn-group">
            <button
              type="button"
              class="btn todo-cancel"
              onClick={setEditable(false)}
            >
              Cancel
              <span class="visually-hidden">renaming {props.element}</span>
            </button>
            <button type="submit" class="btn btn__primary todo-edit">
              Save
              <span class="visually-hidden">new name for {props.element}</span>
            </button>
          </div>
        </form>
      ) : (
        <div className="stack-small">
          <div className="c-cb">
            <input id="todo-0" type="checkbox" defaultChecked="" />
            <label className="todo-label" htmlFor="todo-0">
              {props.element}
            </label>
          </div>
          <div className="btn-group">
            <button type="button" className="btn" onClick={setEditable(true)}>
              Edit <span className="visually-hidden">{props.element}</span>
            </button>
            <button type="button" className="btn btn__danger">
              Delete <span className="visually-hidden">{props.element}</span>
            </button>
          </div>
        </div>
      )}
    </li>
  );
};

export default Todo;
