import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import { catsService } from "../../Services/CatsService";

const fetchCats = createAsyncThunk("cats/fetchCats", async () => {
  const response = await catsService.get(
    "https://api.thecatapi.com/v1/images/search"
  );
  return response;
});

const catsSlice = createSlice({
  name: "cats",
  initialState: {
    cats_list: {},
    current_page: 0,
    isLoading: true,
    gotError: false,
  },
  reducers: {
    changePage(state, action) {
      state.current_page = action.payload;
      if (state.cats_list[state.current_page] === undefined) {
        state.isLoading = true;
      }
    },
  },
  extraReducers: (builder) => {
    builder.addCase(getAllCats.pending, (state) => {
      state.isLoading = true;
      state.gotError = false;
    });
    builder.addCase(getAllCats.fulfilled, (state, action) => {
      state.cats_list[state.current_page] = action.payload;
      state.isLoading = false;
      state.gotError = false;
    });
    builder.addCase(getAllCats.rejected, (state) => {
      state.isLoading = false;
      state.gotError = true;
    });
  },
});

export default catsSlice.reducer;
